import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import { getToken, setToken } from '../helpers/helpers';
import { useState } from 'react';
import { addQoute } from '../fetch';  

const AddQuote = () =>{
    const [inputs, setInputs] = useState({});
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e)=>{
        const name = e.target.name;
        const value = e.target.value;
        setInputs(values => ({...values, [name]: value}));

    };

    const handleSubmit = async (e) =>{
        e.preventDefault();
        const quote = 
        {
            quote: inputs.quote, 
            author: inputs.author,
            book: inputs.book,
        };
        console.log(quote);
        const token = getToken();
        const response = await addQoute(quote, token);

        if(response){
            navigate("/");
        }else {
            setIsError(true);
        }
    };
    return (
        <Form id="form" onSubmit={handleSubmit}>
            <div class="form-group">
                <label for="formGroupAddQuoteInput">Quote</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddQuoteInput" 
                placeholder="Quote"
                value={inputs.quote || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddQuoteInput2">Author</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddQuoteInput2" 
                placeholder="Author"
                value={inputs.author || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddQuoteInput3">Book</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddQuoteInput3" 
                placeholder="Book"
                value={inputs.book || ""} 
                onChange={handleChange}
                />
            </div>
            <Button type="submit" class="btn btn-primary">Submit</Button>
            <Button as={Link} to="/">Cancel</Button>
        </Form>
    );
}; 
export default AddQuote;
