import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckSquare, faCoffee, faAppleWhole } from '@fortawesome/free-solid-svg-icons';
import Button from 'react-bootstrap/Button';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Table from 'react-bootstrap/Table';
const Home = () => {

    const handleOnSelect = () =>{

    }

    return (
        <>
        <div>
        <Tabs
        defaultActiveKey="Home"
        id="fill-tab-example"
        className="mb-3"
        onSelect={() => handleOnSelect}
      >
        <Tab eventKey="Home" title="Books">
        </Tab>
        <Tab eventKey="Quotes" title="Quotes">
        </Tab>
      </Tabs>
      <Button variant="primary">Add a book</Button>
      </div>

        <div className="table-responsive-sm">
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    {booksKeys.map((name, index)=>(<th key={index} scope="col">{name}</th>))}
                    </tr>
                </thead>
                <tbody>
            {
                books.map((book, index)=>(
                    <tr key={index}>
                    <th scope="row">{book.id}</th>
                    <td >{book.title}</td>
                    <td>{book.description}</td>
                    <td >{book.publication_date}</td>
                    <td>{book.publisher}</td>
                    <td >{book.ISBN}</td>
                    <td>{book.pages}</td>
                    <td >{book.genre}</td>
                    <td>{book.date_added}</td>
                    <td><Button variant="outline-primary">Edit</Button></td>
                    <td><Button variant="outline-danger">Delete</Button></td>
                    </tr>        

                )
            )
            }
             </tbody>
             </table>

        </div>
        </>

    );
  };
  export default Home;

  const books = 
  [
    {
        "id": "1",
        "title": "book",
        "description": "desc",
