        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    },
    {
        "id": "2",
        "title": "bookbook",
        "publication_date": "2021-11-01",
        "description": "desc",
        "publisher": "ABZ",
        "ISBN": "971827554412",
        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    }
];

