import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckSquare, faCoffee, faAppleWhole } from '@fortawesome/free-solid-svg-icons';
import Button from 'react-bootstrap/Button';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useRef, useState } from 'react';
import { getToken, getUser } from '../helpers/helpers';
import { getBooks, deleteBook } from '../fetch';  

const books = await getBooks();

const Home = () => {
    const [booksData, setBooksData] = useState(books);
    const isLoggedIn = getUser();
    const [activeDeleting, setActiveDeleting] = useState(null);
    const navigate = useNavigate();

    const handleDelete = (id) => {
        setActiveDeleting(id);
        const token = getToken();
        deleteBook(id, token);
        setTimeout(() => {
            return setActiveDeleting(null);

        }, 2000);
        const books = booksData.filter((book)=> book.id !== id);
        setBooksData(books);
    };
    console.log(activeDeleting);
    return (
        <>
        <div>
            <Tabs
            defaultActiveKey="Home"
            id="fill-tab-example"
            className="mb-3"
            onSelect={(eventKey) => {
                if (eventKey === "Quotes") navigate("/quotes");
            }}
            >
            <Tab eventKey="Home" title="Books">
            </Tab>
            <Tab eventKey="Quotes" title="Quotes">
            </Tab>
            </Tabs>

      </div>

        <div className="table-responsive-sm">
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    {booksKeys.map((name, index)=>(<th key={index} scope="col">{name}</th>))}
                    </tr>
                </thead>

                <Button as={Link} to="/addBook">Add Book</Button> 


                <tbody>
            {
                booksData && booksData.map((book, index)=>(
                    <tr className={`${activeDeleting === book.id ? "table-danger" : ""}`} key={index}>
                    <th scope="row">{index + 1}</th>
                    <td >{book.title}</td>
                    <td>{book.author}</td>
                    <td >{book.publication_date}</td>
                    <td>{book.publisher}</td>
                    <td >{book.isbn}</td>
                    <td>{book.pages}</td>
                    <td >{book.genre}</td>
                    <td>{book.date_added}</td>
                    <td><Button variant="outline-primary" onClick={() => navigate('editBook', {id: book.id})}>Edit</Button></td>

                    {isLoggedIn ? (
                        <td><Button onClick={() => handleDelete(book.id)} variant="outline-danger">Delete</Button></td>
                    ):(
                        <td><Button variant="outline-danger"as={Link} to="/signin">Delete</Button></td>
                    )
                    }
                    </tr>        

                )
            )
            }
            </tbody>


            </table>

        </div>
        </>

    );
  };
  export default Home;

  const book = 
  [
    {
        "title": "book",
        "author": "desc",
        "publication_date": "2021-11-01",
        "publisher": "ABZ",
        "isbn": "971827554412",
        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    },
    {
        "title": "bookbook",
        "publication_date": "2021-11-01",
        "author": "desc",
        "publisher": "ABZ",
        "isbn": "971827554412",
        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    }
];

const booksKeys = Object.keys(book[0]);
