import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckSquare, faCoffee, faAppleWhole } from '@fortawesome/free-solid-svg-icons';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
const Home = () => {

    return (

        <div class="container">
            {
                books.map((book, index)=>(
                    <Card key={index} className="text-center py-5">
                        <Card.Header>{book.id}</Card.Header>
                        <Card.Body>
                            <Card.Title>{book.title}</Card.Title>
                            <Card.Text>
                            {book.description}
                            </Card.Text>
                            <Button variant="primary">Edit</Button>
                            <Button variant="primary">Delete</Button>
                        </Card.Body>
                        <Card.Footer className="text-muted">{book.date_added}</Card.Footer>
                    </Card>
                )
            )
            }
      </div>
    );
  };
  export default Home;

