import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Link, useNavigate } from "react-router";
import { useState } from 'react';
import { setToken, setUser } from '../helpers/helpers';

const SignIn = () => {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();

    async function loginUser(credentials) {
        try {
            const response = await fetch('http://localhost:5252/api/Auth/login', {
                method: 'POST',
                mode: 'cors', // Allow cross-origin requests
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(credentials)
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const data = await response.json(); // Parse JSON response
            return data; // Return the response data
        } catch (error) {
            console.error('Error logging in:', error);
            return null; // Return null if an error occurs
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log(JSON.stringify({
            username,
            password
        }));
        const token = await loginUser({
            username, // Ensure these variables are defined in your component's state
            password
        });

        if (token) {
            console.log('Token:', token); // Log the token
        } else {
            console.error('Login failed.');
        }
    };
    console.log(username);
    console.log(password);
    return (

        <Form onSubmit={handleSubmit}>
            <div class="form-group">
                <label for="UsernameInput">Username</label>
                <input 
                type="text" 
                class="form-control" 
                id="UsernameInput" 
                aria-describedby="UsernameInput" 
                placeholder="Enter username"
                value={username}
                onChange={(e)=>setUserName(e.target.value)}
                />
            </div>
            <div class="form-group">
                <label for="PasswordInput">Password</label>
                <input 
                type="password" 
                class="form-control" 
                id="PasswordInput" 
                placeholder="Password"
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                />
                {isError ? "Wrong username or password, try again" : null}
                <small id="formText" class="form-text text-muted"><Link to={""}>Forgot password?</Link></small>
            </div>
            <Button type="submit" class="btn btn-primary">Submit</Button>
            <div>
                <p id="SignUpText" class="form-text text-muted"><Link to={"/signup"}>Create new account!</Link></p>
            </div>        
        </Form>

    );
  };
  export default SignIn;
