import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import { getToken, setToken } from '../helpers/helpers';
import { useState } from 'react';
import { addBook } from '../fetch';  



const AddBook = ()=>{
    const dateAdded = new Date().toLocaleDateString();
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();


    const handleSubmit = async (e) =>{
        e.preventDefault();
        const form = document.getElementById("form");
        const formData= new FormData(form);
        const book = 
        {
            title: formData.title, 
            author: formData.author,
            publication_date: formData.publication_date,
            publisher: formData.publisher,
            isbn: formData.isbn,
            pages: formData.pages,
            genre: formData.genre,
            date_added: dateAdded
        }
        const token = getToken();
        const response = await addBook(book, token);

        if(response){
            navigate("/");
        }else {
            setIsError(true);
        }
    };

    return (

        <Form id="form" onSubmit={handleSubmit}> 
            <div class="form-group">
                <label for="formGroupAddBookInput">Title</label>
                <input type="text" class="form-control" id="formGroupAddBookInput" placeholder="Title" required/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput2">Author</label>
                <input type="text" class="form-control" id="formGroupAddBookInput2" placeholder="Author"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput3">Publication Date</label>
                <input type="date" class="form-control" id="formGroupAddBookInput3" placeholder="Publication Date"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput4">Publisher</label>
                <input type="text" class="form-control" id="formGroupAddBookInput4" placeholder="Publisher"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput5">ISBN</label>
                <input type="number" class="form-control" id="formGroupAddBookInput5" placeholder="ISBN"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput6">Pages</label>
                <input type="number" class="form-control" id="formGroupAddBookInput6" placeholder="Pages"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput7">Genre</label>
                <input type="text" class="form-control" id="formGroupAddBookInput7" placeholder="Genre"/>
            </div>
            <div>
                <input type="hidden" class="form-control" id="formGroupAddBookInput8" value={dateAdded}/>
            </div>

            <Button type="submit" class="btn btn-primary">Submit</Button>
            <Button as={Link} to="/">Cancel</Button>
        </Form>

    );

};
  export default AddBook;
