import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import { getToken, setToken } from '../helpers/helpers';
import { useState } from 'react';
import { addQoute } from '../fetch';  

const AddQuote = () =>{
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();


    const handleSubmit = async (e) =>{
        e.preventDefault();
        const form = document.getElementById("form");
        const formData= new FormData(form);
        const quote = 
        {
            quote: formData.quote, 
            author: formData.author,
            book: formData.book,
        }
        const token = getToken();
        const response = await addQoute(quote, token);

        if(response){
            navigate("/");
        }else {
            setIsError(true);
        }
    };
    return (
        <Form onSubmit={handleSubmit}>
            <div class="form-group">
                <label for="formGroupAddQuoteInput">Quote</label>
                <input type="text" class="form-control" id="formGroupAddQuoteInput" placeholder="Quote"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddQuoteInput2">Author</label>
                <input type="text" class="form-control" id="formGroupAddQuoteInput2" placeholder="Author"/>
            </div>
            <div class="form-group">
                <label for="formGroupAddQuoteInput3">Book</label>
                <input type="text" class="form-control" id="formGroupAddQuoteInput3" placeholder="Book"/>
            </div>
            <Button type="submit" class="btn btn-primary">Submit</Button>
            <Button as={Link} to="/">Cancel</Button>
        </Form>
    );
}; 
export default AddQuote;
