import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Link, useNavigate } from "react-router";
import { useState } from 'react';
import { setToken, setUser } from '../helpers/helpers';

const SignIn = () => {
    const [userName, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();

    async function loginUser(credentials) {
        return fetch('http://localhost:5252/api/Auth/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(credentials)
        })
          .then(data => data.json())
       }
    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = await loginUser({
            userName,
            password
        });
        console.log(token);
        navigate("/");
        setToken(token);
        setUser(true);

    }
    console.log(userName);
    console.log(password);
    return (

        <Form onSubmit={handleSubmit}>
            <div class="form-group">
                <label for="UsernameInput">Username</label>
                <input 
                type="text" 
                class="form-control" 
                id="UsernameInput" 
                aria-describedby="UsernameInput" 
                placeholder="Enter username"
                value={userName}
                onChange={(e)=>setUserName(e.target.value)}
                />
            </div>
            <div class="form-group">
                <label for="PasswordInput">Password</label>
                <input 
                type="password" 
                class="form-control" 
                id="PasswordInput" 
                placeholder="Password"
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                />
                {isError ? "Wrong username or password, try again" : null}
                <small id="formText" class="form-text text-muted"><Link to={""}>Forgot password?</Link></small>
            </div>
            <Button type="submit" class="btn btn-primary">Submit</Button>
            <div>
                <p id="SignUpText" class="form-text text-muted"><Link to={"/signup"}>Create new account!</Link></p>
            </div>        
        </Form>

    );
  };
  export default SignIn;
