import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Link, useNavigate } from "react-router";
import { useState } from 'react';
import { setToken, setUser } from '../helpers/helpers';
import { loginUser } from '../fetch';  

const SignIn = () => {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = await loginUser({
            username, // Ensure these variables are defined in your component's state
            password
        });

        if (token) {
            setToken(token);
            setUser(true);
            navigate("/");
        } else {
            console.error('Login failed.');
            setIsError(true);
        }
    };

    return (

        <Form onSubmit={handleSubmit}>
            <div class="form-group">
                <label for="UsernameInput">Username</label>
                <input 
                type="text" 
                class="form-control" 
                id="UsernameInput" 
                aria-describedby="UsernameInput" 
                placeholder="Enter username"
                value={username}
                onChange={(e)=>setUserName(e.target.value)}
                />
            </div>
            <div class="form-group">
                <label for="PasswordInput">Password</label>
                <input 
                type="password" 
                class="form-control" 
                id="PasswordInput" 
                placeholder="Password"
                value={password}
                onChange={(e)=>setPassword(e.target.value)}
                />
                {isError ? "Wrong username or password, try again" : null}
            </div>
            <p id="formText" class="form-text text-muted"><Link to={""}>Forgot password?</Link></p>
            <Button type="submit" class="btn btn-primary">Submit</Button>
            <div>
                <p id="SignUpText" class="form-text text-muted"><Link to={"/signup"}>Create new account!</Link></p>
            </div>        
        </Form>

    );
  };
  export default SignIn;
