import { Button, Form } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import { getToken, setToken } from '../helpers/helpers';
import { useState } from 'react';
import { addBook } from '../fetch';  



const AddBook = ()=>{
    const dateAdded = new Date().toLocaleDateString();
    const [isError, setIsError] = useState(false);
    const navigate = useNavigate();
    const [inputs, setInputs] = useState({});

    const handleChange = (e)=>{
        const name = e.target.name;
        const value = e.target.value;
        setInputs(values => ({...values, [name]: value}));

    };

    const handleSubmit = async (e) =>{
        e.preventDefault();
        const book = 
        {
            title: inputs.title, 
            author: inputs.author,
            publication_date: inputs.publication_date,
            publisher: inputs.publisher,
            isbn: inputs.isbn,
            pages: inputs.pages,
            genre: inputs.genre,
            date_added: dateAdded
        };
        const token = getToken();
        const response = await addBook(book, token);

        if(response){
            navigate("/");
        }else {
            setIsError(true);
        }
    };
    console.log(inputs);
    return (

        <Form id="form" onSubmit={handleSubmit}> 
            <div class="form-group">
                    <label for="formGroupAddBookInput">Title</label>
                    <input 
                    type="text" 
                    class="form-control" 
                    id="formGroupAddBookInput" 
                    placeholder="Title"
                    name="title"
                    value={inputs.title || ""} 
                    onChange={handleChange}
                    />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput2">Author</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddBookInput2" 
                placeholder="Author"
                name="author"
                value={inputs.author || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput3">Publication Date</label>
                <input 
                type="date" 
                class="form-control" 
                id="formGroupAddBookInput3" 
                placeholder="Publication Date"
                name="publication_date"
                value={inputs.publication_date || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput4">Publisher</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddBookInput4" 
                placeholder="Publisher"
                name="publisher"
                value={inputs.publisher || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput5">ISBN</label>
                <input 
                type="number" 
                class="form-control" 
                id="formGroupAddBookInput5" 
                placeholder="ISBN"
                name="isbn"
                value={inputs.isbn || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput6">Pages</label>
                <input 
                type="number" 
                class="form-control" 
                id="formGroupAddBookInput6" 
                placeholder="Pages"
                name="pages"
                value={inputs.pages || ""} 
                onChange={handleChange}
                />
            </div>
            <div class="form-group">
                <label for="formGroupAddBookInput7">Genre</label>
                <input 
                type="text" 
                class="form-control" 
                id="formGroupAddBookInput7" 
                placeholder="Genre"
                name="genre"
                value={inputs.genre || ""} 
                onChange={handleChange}
                />
            </div>
            <div>
                <input 
                type="hidden" 
                class="form-control" 
                id="formGroupAddBookInput8" 
                value={dateAdded}/>
            </div>

            <Button type="submit" class="btn btn-primary">Submit</Button>
            <Button as={Link} to="/">Cancel</Button>
        </Form>

    );

};
  export default AddBook;
