import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheckSquare, faCoffee, faAppleWhole } from '@fortawesome/free-solid-svg-icons';
import Button from 'react-bootstrap/Button';
import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
const Home = () => {


    return (
        <>
        <div><Tabs
        defaultActiveKey="profile"
        id="fill-tab-example"
        className="mb-3"
        fill
      >
        <Tab eventKey="home" title="Home">
          Tab content for Home
        </Tab>
        <Tab eventKey="profile" title="Profile">
          Tab content for Profile
        </Tab>
      </Tabs></div>

        <div className="table-responsive-sm">
            <table className="table">
                <thead>
                    <tr>
                    <th scope="col">#</th>
                    {booksKeys.map((name, index)=>(<th key={index} scope="col">{name}</th>))}
                    </tr>
                </thead>
                <tbody>
            {
                books.map((book, index)=>(
                    <tr key={index}>
                    <th scope="row">{book.id}</th>
                    <td >{book.title}</td>
                    <td>{book.description}</td>
                    <td >{book.publication_date}</td>
                    <td>{book.publisher}</td>
                    <td >{book.ISBN}</td>
                    <td>{book.pages}</td>
                    <td >{book.genre}</td>
                    <td>{book.date_added}</td>
                    <td><Button variant="outline-primary">Edit</Button></td>
                    <td><Button variant="outline-danger">Delete</Button></td>
                    </tr>        

                )
            )
            }
             </tbody>
             </table>

        </div>
        </>

    );
  };
  export default Home;

  const books = 
  [
    {
        "id": "1",
        "title": "book",
        "description": "desc",
        "publication_date": "2021-11-01",
        "publisher": "ABZ",
        "ISBN": "971827554412",
        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    },
    {
        "id": "2",
        "title": "bookbook",
        "publication_date": "2021-11-01",
        "description": "desc",
        "publisher": "ABZ",
        "ISBN": "971827554412",
        "pages": 100,
        "genre": "Fiction",
        "date_added": "2021-11-01" 
    }
];

const booksKeys = Object.keys(books[0]);
